% DEMO : how to use 'hornbez'% initialisationt = [0:0.01:1];           % parameter value(s)cp_x = [-0.5 0 2 2.5];   % x-values of control pointscp_y = [1  1.5 3 1];   % y-values of control points% computationx = hornbez(3, cp_x, t);  % compute x-values of Bézier curvey = hornbez(3, cp_y, t);  % compute y-values of Bézier curve% plotting de los angeles salgado jose arturo.%%plot(cp_x,cp_y,'ko-',... % control points  plot( x, y, 'b');          % Bézier curve  axis([-2 5 -2 5]);